import React from 'react';

import './HomePage.css'; 

function AboutUs() {
  return (
    <div className="about-us-container">
      <h1>About Us</h1>
      <p>
        Welcome to The Bookshelf, your go-to place for discovering and enjoying a wide range of books. 
        Our mission is to provide a platform where every story finds a home, whether it's fiction, non-fiction, romance, or historical fiction.
      </p>
      <p>
        Our web app is designed to make it easy for you to browse, order, and enjoy your favorite books. 
        With a user-friendly interface and a wide selection, The Bookshelf is dedicated to creating an enjoyable reading experience for all book lovers.
      </p>
      <p>
        We are passionate about books and believe in the power of reading to inspire, educate, and entertain. 
        Thank you for visiting The Bookshelf. Happy reading!
      </p>
    </div>
  );
}

export default AboutUs;
