import React, { useState } from 'react';
import axios from 'axios';
import './AddBookToCartPage.css';
import { useNavigate } from 'react-router-dom';
//import { userId } from './users';



function AddBookToCartPage({ userId }) {
    const navigate = useNavigate();

   // Example function using userId
   const handleAddToCart = () => {
        // Your logic for adding a book to the cart
        
        // Redirect to the BookList page
        navigate('/booklist');
    };


   const [bookId, setBookId] = useState('');
    const [quantity, setQuantity] = useState('');
    const [error, setError] = useState(null);

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const response = await axios.post(`http://localhost:8080/carts/${userId}/books/${bookId}?quantity=${quantity}`);

            if (response.status === 200) {
                alert('Book added to cart successfully!');
            }
        } catch (err) {
            setError('Failed to add the book to the cart. Please try again.');
            console.error(err);
        }
    };

    return (
 
         <div className="add-to-cart-container">
            <h2>Add Book to Cart</h2>
            <form onSubmit={handleSubmit}>
                <div className="form-group">
                    <label>Book ID</label>
                    <input
                        type="text"
                        className="form-control"
                        value={bookId}
                        onChange={(e) => setBookId(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label>Quantity</label>
                    <input
                        type="number"
                        className="form-control"
                        value={quantity}
                        onChange={(e) => setQuantity(e.target.value)}
                        required
                    />
                </div>
                <div>
                 <button onClick={handleAddToCart}>Add to Cart</button>
                </div>
                <button type="submit" className="btn btn-primary">Add to Cart</button>
                {error && <p className="text-danger mt-2">{error}</p>}
            </form>
        </div>
    );
};

export default AddBookToCartPage;
