import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ViewOrders.css';
<Route path="view-orders" element={<ViewOrders />} /> 

const ViewOrders = () => {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    // Replace `userId` with the actual user ID, possibly from authentication context
    const userId = 1;

    axios.get(`http://localhost:8080/users/${userId}/orders`)
      .then(response => {
        setOrders(response.data);
        setLoading(false);
      })
      .catch(error => {
        setError(error);
        setLoading(false);
        console.error('Error fetching orders:', error);
      });
  }, []);

  if (loading) {
    return <p>Loading orders...</p>;
  }

  if (error) {
    return <p>Error fetching orders: {error.message}</p>;
  }

  return (
    <div>
      <h2>Your Orders</h2>
      {orders.length === 0 ? (
        <p>No orders found.</p>
      ) : (
        <ul>
          {orders.map(order => (
            <li key={order.orderId}>
              Order ID: {order.orderId}, Total Amount: ${order.totalAmount}, Status: {order.status}, Order Date: {new Date(order.orderDate).toLocaleDateString()}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

export default ViewOrders;
